Enemy::Enemy(uint8_t initialX, uint16_t initialY, Adafruit_ILI9341 *lcd, uint8_t type)
{
    x = initialX;
    y = initialY;
    LCD = lcd;
    Enemy::type = type;
}

void Enemy::drawEnemy()
{
    if (type == 0)
    {
        LCD->fillRect((x - drawOffset) + xOffset, (y - drawOffset) + yOffset, 30, 30, ILI9341_BLACK);
        return;
    }
    for (int drawY = 0; drawY < ENEMY_HEIGHT; ++drawY)
    {
        for (int drawX = 0; drawX < ENEMY_WIDTH; ++drawX)
        {
            // Draw pixel using the color value in the array
            // TODO: Remove turning off the interupt and just turn off the timer
            cli();
            LCD->drawPixel(((x - drawOffset) + xOffset + drawX), (y - drawOffset + yOffset + drawY), pgm_read_word(&enemyArt[type - 1][drawY][drawX]));
            sei();
        }
    }

    // clear next to the player
    LCD->fillRect((x - (drawOffset +4)) + xOffset, (y - drawOffset) + yOffset, 4, 30, ILI9341_BLACK);
}
/**
 * @brief Clears the screen where the enemies are, this is used when the enemies move to the next row
 * We do this because the enemies leave some pixels behind when they move a row down
 * TODO: find a better solition for this, the current method causes a little lagspike
 */
void Enemy::moveRowFix()
{
    LCD->fillRect(0, 20, 230, 210, ILI9341_BLACK);
}

/**
 * @return a random number for the enemy type
 * When the game is in simgleplayer mode, the enemies types will be between 1 and 4
 * When the game is in multiplayer mode, all enemy types will be used
 * TODO: implement multiplayer mode
*/
static uint8_t Enemy::getRandomType()
{
    return rand() % ENEMY_TYPES_AMOUNT + 1;
}


static void Enemy::moveEnemy(Enemy (*enemiesArray)[5], uint8_t timemovement, uint8_t maxTimeMovement)
{
    {
        shouldDrawEnemy = 4;
        // Check if enemies can't move horizontally anymore
        if (timemovement == (maxTimeMovement - 1))
        {
            
            // Move enemies to the next row
            enemiesArray[0][0].moveRowFix();
            for (uint8_t j = maxEnemyRows - 1; j > 0; j--)
            {
                for (uint8_t i = 0; i < maxEnemyColumns; i++)
                {
                    // copy row above
                    enemiesArray[j][i].setType(enemiesArray[j - 1][i].getType());
                }
            }
            for (uint8_t i = 0; i < maxEnemyColumns; i++)
            {
                // set type of new row
                enemiesArray[0][i].setType(getRandomType());
            }
            // Reset timemovement
            timemovement = 0;
        }
        // Move enemies horizontally
        for (uint8_t j = 0; j < maxEnemyRows; j++)
        {
            for (uint8_t i = 0; i < maxEnemyColumns; i++)
            {
                enemiesArray[j][i].setXOffset((i * 40) + (4 * timemovement));
                enemiesArray[j][i].setYOffset((j * 50));
                //! enable this to draw the enemies instantly after moving
                // enemiesArray[j][i].drawEnemy();
            }
        }
    }
}

// setters
void Enemy::setType(uint8_t type)
{
    Enemy::type = type;
}

void Enemy::setXOffset(uint8_t newXOffset)
{
    xOffset = newXOffset;
}

void Enemy::setYOffset(uint16_t newYOffset)
{
    yOffset = newYOffset;
}

// getters

uint8_t Enemy::getXPosition()
{
    return (x - drawOffset) + xOffset;
}

uint16_t Enemy::getYPosition()
{
    return (y - drawOffset) + yOffset;
}

uint8_t Enemy::getType()
{
    return type;
}